import { createApp } from 'vue'
import App from './App.vue'

/* import font awesome icon component */
import '@fortawesome/fontawesome-free/css/all.css'
import '@fortawesome/fontawesome-free/js/all.js'

// Vue-Router -----------------------------------------------------------------------------------------
import { createRouter, createWebHashHistory } from 'vue-router';
import HomePage from './pages/HomePage.vue';

const routes = [
    { path: '/', component: HomePage },
    // { path: '/RestaurantView/:name', name: 'RestaurantView', component: RestaurantView },
]

const router = createRouter({
    history: createWebHashHistory(),
    routes,
})



// i18n ------------------------------------------------------------------------------------------------
import { createI18n } from 'vue-i18n'

import {textIntroFooter_fr} from '../data/data.js'
import {textIntroFooter_en} from '../data/data.js'

console.log(textIntroFooter_fr);


const i18n = createI18n({
  legacy: false,
  locale: 'en',
  messages: {
    en: {
      welcome: 'Welcome',
      about: 'About',
      contact: 'Contact',
      introFooter: textIntroFooter_en
      // autres clés et traductions pour l'anglais
    },
    fr: {
      welcome: 'Bienvenue',
      about: 'À propos',
      contact: 'Contactez-nous',
      introFooter: textIntroFooter_fr
      // autres clés et traductions pour le français
    }
    // ajoutez d'autres langues si nécessaire
  }
})



const VueApp = createApp(App);
VueApp.use(i18n);
VueApp.use(router);
VueApp.mount('#app');
