import { createStore } from 'vuex';

export default createStore ({
    state: {
        connected: false,
    },
    getters: {

        getConnected(state) {
            return state.connected
        }
    },
    mutations: {
        isConnected(state) {
            state.connected = true;
        }

    },
    actions: {

    },
    modules: {

    }
})
