export const validateEmail = (value) => {

    return (/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(.\w{2,3})+$/.test(value));
}

export const replace_newLine_to_br_tags = (text) => {

    return text.replace(/\n/g, '<br>');
}
